import React from 'react';

const VideoPlayer = ({ dubbedUrl, englishText, translatedText }) => {
  if (!dubbedUrl) return null;

  const langName = dubbedUrl.includes('hi') ? 'Hindi' : dubbedUrl.includes('ta') ? 'Tamil' : 'Telugu';

  return (
    <div className="player-section">
      <h3>English Original:</h3>
      <p>{englishText}</p>
      <h3>Translated ({langName}):</h3>
      <p>{translatedText}</p>
      <video controls src={`http://localhost:5000${dubbedUrl}`} width="100%" />
      <a href={`http://localhost:5000${dubbedUrl}`} download>Download Dubbed Video</a>
    </div>
  );
};

export default VideoPlayer;
