import React, { useState } from 'react';
import UploadForm from './components/UploadForm';
import VideoPlayer from './components/VideoPlayer';
import './App.css';

function App() {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSuccess = (data) => {
    setResult(data);
    setLoading(false);
    setError(null);
  };

  const handleError = (msg) => {
    setError(msg);
    setLoading(false);
  };

  const handleLoading = (isLoading) => {
    setLoading(isLoading);
  };

  const reset = () => {
    setResult(null);
    setError(null);
  };

  return (
    <div className="App">
      <header className="header">
        <h1>English to Indian Languages Video Dubber</h1>
        <p>Dub your English videos in <strong>Hindi, Tamil, Telugu</strong> with AI</p>
      </header>

      <main className="main-content">
        {!result ? (
          <div className="upload-section">
            <UploadForm
              onSuccess={handleSuccess}
              onError={handleError}
              onLoading={handleLoading}
            />
            {loading && (
              <div className="loading">
                <div className="spinner"></div>
                <p>Processing your video... This may take 30â€“90 seconds.</p>
              </div>
            )}
            {error && (
              <div className="error">
                <p>Error: {error}</p>
                <button onClick={reset} className="retry-btn">Try Again</button>
              </div>
            )}
          </div>
        ) : (
          <div className="result-section">
            <VideoPlayer {...result} />
            <button onClick={reset} className="new-dub-btn">
              Dub Another Video
            </button>
          </div>
        )}
      </main>

      <footer className="footer">
        <p>Made with <span>AI</span> in India | @raaid0711</p>
      </footer>
    </div>
  );
}

export default App;
